// Набір базових фігур Block-Blast-подібних (без обертання, як в оригіналі)
const SHAPES = [
    // 2 клітини (лінії)
    [{ x: 0, y: 0 }, { x: 1, y: 0 }],
    [{ x: 0, y: 0 }, { x: 0, y: 1 }],
    // 3 клітини (лінії)
    [{ x: 0, y: 0 }, { x: 1, y: 0 }, { x: 2, y: 0 }],
    [{ x: 0, y: 0 }, { x: 0, y: 1 }, { x: 0, y: 2 }],
    // 4 клітини (лінії)
    [{ x: 0, y: 0 }, { x: 1, y: 0 }, { x: 2, y: 0 }, { x: 3, y: 0 }],
    [{ x: 0, y: 0 }, { x: 0, y: 1 }, { x: 0, y: 2 }, { x: 0, y: 3 }],
    // Квадрат 2×2
    [{ x: 0, y: 0 }, { x: 1, y: 0 }, { x: 0, y: 1 }, { x: 1, y: 1 }],
    // Т-подібна
    [{ x: 0, y: 0 }, { x: 1, y: 0 }, { x: 2, y: 0 }, { x: 1, y: 1 }],
    // └ L-триміно (3 клітини) — усі 4 орієнтації
    [{ x: 0, y: 0 }, { x: 0, y: 1 }, { x: 1, y: 1 }],
    [{ x: 0, y: 1 }, { x: 1, y: 0 }, { x: 1, y: 1 }],
    [{ x: 0, y: 0 }, { x: 1, y: 0 }, { x: 1, y: 1 }],
    [{ x: 0, y: 0 }, { x: 0, y: 1 }, { x: 1, y: 0 }],
    // L-тетроміно (4 клітини) — 4 повороти
    [{ x: 0, y: 0 }, { x: 0, y: 1 }, { x: 0, y: 2 }, { x: 1, y: 2 }],
    [{ x: 0, y: 1 }, { x: 1, y: 1 }, { x: 2, y: 0 }, { x: 2, y: 1 }],
    [{ x: 0, y: 0 }, { x: 1, y: 0 }, { x: 1, y: 1 }, { x: 1, y: 2 }],
    [{ x: 0, y: 0 }, { x: 0, y: 1 }, { x: 1, y: 0 }, { x: 2, y: 0 }],
    // Дзеркальна L (J-тетроміно) — 4 повороти
    [{ x: 0, y: 2 }, { x: 1, y: 0 }, { x: 1, y: 1 }, { x: 1, y: 2 }],
    [{ x: 0, y: 0 }, { x: 1, y: 0 }, { x: 2, y: 0 }, { x: 2, y: 1 }],
    [{ x: 0, y: 0 }, { x: 0, y: 1 }, { x: 0, y: 2 }, { x: 1, y: 0 }],
    [{ x: 0, y: 0 }, { x: 0, y: 1 }, { x: 1, y: 1 }, { x: 2, y: 1 }],
    // 2-линії
    //[{x:0,y:0},{x:1,y:0}],
    // [{x:0,y:0},{x:0,y:1}],
    // 3-линії
    // [{x:0,y:0},{x:1,y:0},{x:2,y:0}],
    // [{x:0,y:0},{x:0,y:1},{x:0,y:2}],
    // // квадрати
    // [{x:0,y:0},{x:1,y:0},{x:0,y:1},{x:1,y:1}],
    // // L- та Г-подібні
    // [{x:0,y:0},{x:0,y:1},{x:1,y:1}],
    // [{x:0,y:0},{x:1,y:0},{x:1,y:1}],
    // // довгі
    // [{x:0,y:0},{x:1,y:0},{x:2,y:0},{x:3,y:0}],
    // [{x:0,y:0},{x:0,y:1},{x:0,y:2},{x:0,y:3}],
    // // Т-подібна
    // [{x:0,y:0},{x:1,y:0},{x:2,y:0},{x:1,y:1}],
];
export function randomPiece() {
    return SHAPES[Math.floor(Math.random() * SHAPES.length)];
}
